// frontend/src/App.js
import React from "react";
import RealApp from "./RealApp"; // <-- your real app root component

function DebugInfo() {
  return (
    <div style={{ padding: 40, fontFamily: "Arial, sans-serif", color: "#0b1220" }}>
      <h1 style={{ marginBottom: 10 }}>⚡ Debug Mode: React rendering OK</h1>
      <p>If you see this, React and the JS bundle are working.</p>
      <ul style={{ lineHeight: 1.6 }}>
        <li><strong>Port:</strong> {window.location.port || "unknown"}</li>
        <li><strong>NODE_ENV:</strong> {process.env.NODE_ENV}</li>
        <li><strong>API_BASE:</strong> {process.env.REACT_APP_API_BASE || "not set"}</li>
        <li><strong>Build time:</strong> {new Date().toLocaleTimeString()}</li>
      </ul>
      <p>Check DevTools → Console for any runtime errors.</p>
    </div>
  );
}

class ErrorBoundary extends React.Component {
  constructor(props) {
    super(props);
    this.state = { error: null };
  }
  static getDerivedStateFromError(error) {
    return { error };
  }
  render() {
    if (this.state.error) {
      return (
        <div style={{ padding: 40, color: "red" }}>
          ❌ React runtime error: {String(this.state.error)}
        </div>
      );
    }
    return this.props.children;
  }
}

export default function App() {
  const isDev = process.env.NODE_ENV === "development";
  return (
    <ErrorBoundary>
      {isDev ? <DebugInfo /> : <RealApp />}
    </ErrorBoundary>
  );
}
